    const submitHandler = (event) => {
        event.preventDefault()

      
    }
    return(
        <form className="form" onSubmit={submitHandler}>
            <Input label="Amount" input={{
                id: 'amount_' + props.id,
                type : "number",
                min: '1',
                max :'5',
                step:'1',
                defaultValue:'1',
            }}/>
            <button>+ add</button>


        </form>
    )

}
export default MealItemForm;
